import 'dart:convert';
import 'dart:io';
import 'dart:typed_data';

import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';
import 'package:permission_handler/permission_handler.dart';

void main() => runApp(MaterialApp(
      debugShowCheckedModeBanner: false,
      title: "Image view",
      theme: ThemeData(primarySwatch: Colors.purple),
      home: MyHome(),
    ));

class MyHome extends StatefulWidget {
  const MyHome({Key? key}) : super(key: key);

  @override
  _MyHomeState createState() => _MyHomeState();
}

class _MyHomeState extends State<MyHome> {
  int index = 0;
  final items = List.generate(100, (counter) => 'Item: $counter');
  String filepath = '';

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text("Image View Demo")),
      body: buildListViews(),
      bottomNavigationBar: BottomNavigationBar(
        backgroundColor: Colors.purpleAccent,
        unselectedItemColor: Colors.white,
        selectedItemColor: Colors.black,
        type: BottomNavigationBarType.fixed,
        currentIndex: index,
        items: const [
          BottomNavigationBarItem(
            icon: Icon(Icons.network_wifi),
            label: 'Network',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.web_asset),
            label: 'Asset',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.file_copy),
            label: 'File',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.memory),
            label: 'Memory',
          ),
        ],
        onTap: (int index) {
          setState(() {
            this.index = index;
          });
        },
      ),
    );
  }

  Widget buildListViews() {
    if (index == 0) {
      return networkImageView();
    } else if (index == 1) {
      return assetImageView();
    } else if (index == 2) {
      return fileImageView();
    } else if (index == 3) {
      return memoryImageView();
    } else {
      return Container();
    }
  }

  // Image from network
  Widget networkImageView() {
    return Center(
      child: Image.network(
          'https://learn.g2.com/hubfs/Stock%20images/Digital%20image%20of%20globe%20with%20conceptual%20icons.%20Globalization%20concept.%20Elements%20of%20this%20image%20are%20furnished%20by%20NASA.jpeg'),
    );
  }

  // Image from Asset
  Widget assetImageView() {
    return Center(
      child: Image.asset(
        'assets/images/network.jpg',
        width: 300,
        height: 300,
        semanticLabel: 'Image from Asset',
        fit: BoxFit.fitHeight,
      ),
    );
  }

  // Image from file
  Widget fileImageView() {
    return Center(
      child: Image.file(File(filepath)),
    );
  }

  Future<void> checkPermission() async {
    if (await Permission.storage.request().isGranted) {
      setState(() {
        // Please set your device's image path
        filepath = '/storage/emulated/0/Download/network-web.png';
      });
    } else {
      await Permission.storage.request();
    }
  }

  // Image from Memory
  Widget memoryImageView() {
    String BASE64_STRING =
        "";
    Uint8List byte = base64Decode(BASE64_STRING);
    return Center(
      child: Image.memory(byte),
    );
  }
}
